export { TileMap } from "./tilemap";
export { TileLayer } from "./tilelayer";
export { TileArea } from "./tilearea";
export { Tile } from "./tile";
export { TileSet } from "./tileset";

/**
 * A tile object containing tile ID, tileset ID and custom properties.
 */
export class Tile {
	/**
	 * Tile constructor.
	 *
	 * @param {object|string} options={} Options for the tile. Can also be tile data you loaded from JSON or a plain string in the following format: <tileId:tilesetId>
	 * @param {number} options.tileId - {@link Tile#tileId}
	 * @param {number} options.tilesetId - {@link Tile#tilesetId}
	 * @param {Map<string, *>} [options.properties={}] - {@link Tile#properties}
	 */
	constructor(options) {
		/** The tile ID of the tile.
		 * @type {number} */
		this.tileId = undefined;

		/** The tileset ID of the tile.
		 * @type {number} */
		this.tilesetId = undefined;

		if (typeof options === "string") {
			const colonIndex = options.indexOf(":");
			this.tileId = parseInt(options.substring(0, colonIndex));
			this.tilesetId = parseInt(options.substring(colonIndex + 1));
		} else {
			this.tileId = options.tileId;
			this.tilesetId = options.tilesetId;
		}

		/** Custom properties of the tile.
		 * @type {Map<string, *>} */
		this.properties = options.properties || new Map();
	}

	/**
	 * Sets the data for this tile.
	 *
	 * @param {number} tileId - The ID to assign to the tile.
	 * @param {number} [tilesetId] - The tileset ID to assign to the tile.
	 * @param {boolean} [replaceEmpty] - Should values less than 0 (-1) be handled (true) or ignored (false).
	 */
	setData(tileId, tilesetId, replaceEmpty) {
		if (tileId >= 0 || replaceEmpty) {
			this.tileId = Math.max(tileId, -1);
		}
		if (tilesetId != null && (tilesetId >= 0 || replaceEmpty)) {
			this.tilesetId = Math.max(tilesetId, -1);
		}
	}

	/**
	 * toJSON function used for JSON serialization.
	 * @see https://developer.mozilla.org/en-US/docs/Web/JavaScript/Reference/Global_Objects/JSON/stringify#toJSON()_behavior
	 *
	 * @returns {Tile|string} Object to be serialized
	 */
	toJSON() {
		if (this.properties.size === 0) {
			return this.tileId + ":" + this.tilesetId;
		} else {
			return this;
		}
	}

	/**
	 * Creates a clone out of this tile.
	 *
	 * @returns {Tile} The new tile.
	 */
	clone() {
		return new Tile(this);
	}
}

/**
 * A tile area object with a size and tiles.
 */
export class TileArea {
	/**
	 * TileArea constructor.
	 *
	 * @param {number} width - {@link TileArea#width}
	 * @param {number} height - {@link TileArea#height}
	 * @param {Tile[]} tiles - {@link TileArea#tiles}
	 */
	constructor(width, height, tiles) {
		/** The width of the tile area.
		 * @type {number} */
		this.width = width;
		/** The height of the tile area.
		 * @type {number} */
		this.height = height;

		/** The tiles in the tile area
		 * @type {Tile[]} */
		this.tiles = tiles;
	}

	/**
	 * Gets the index of a tile in the tile array from a position.
	 *
	 * @param {number} x - The x coordinate of the tile.
	 * @param {number} y - The y coordinate of the tile.
	 *
	 * @returns {number} The index of the tile in the main array.
	 */
	getTileIndex(x, y) {
		if (!this._inRange(x, y)) {
			throw new RangeError();
		}
		return y * this.width + x;
	}

	/**
	 * Gets an area of tiles.
	 *
	 * @param {number} x - The x coordinate of the tile area.
	 * @param {number} y - The y coordinate of the tile area.
	 * @param {number} width - The width of the tile area.
	 * @param {number} height - The height of the tile area.
	 *
	 * @returns {TileArea} A {@link TileArea} of the area requested.
	 */
	getTileArea(x, y, width, height) {
		if (!this._inRange(x, y, width, height)) {
			throw new RangeError();
		}
		const tiles = [];
		for (let ly = y; ly < y + height; ly++) {
			for (let lx = x; lx < x + width; lx++) {
				tiles.push(this.tiles[this.getTileIndex(lx, ly)]);
			}
		}
		return new TileArea(width, height, tiles);
	}

	/**
	 * Inserts a TileArea into this TileArea.
	 *
	 * This isertion does not fail if it goes outside of the TileArea.
	 *
	 * @param {number} x - Top left x coordinate of where the TileArea should be inserted.
	 * @param {number} y - Top left y coordinate of where the TileArea should be inserted.
	 * @param {TileArea} tileArea - The TileArea to be inserted.
	 * @param {boolean} skipEmpty - If the insertion should skip empty (-1) tiles.
	 */
	insertTileArea(x, y, tileArea, skipEmpty) {
		for (let lx = x; lx < x + tileArea.width; lx++) {
			for (let ly = y; ly < y + tileArea.height; ly++) {
				try {
					const newTile = tileArea.tiles[tileArea.getTileIndex(lx - x, ly - y)];
					if (skipEmpty && newTile.tileId === -1) continue;
					this.tiles[this.getTileIndex(lx, ly)] = newTile;
				} catch (e) {
					if (e instanceof RangeError) continue; else throw e;
				}
			}
		}
	}

	_inRange(x, y, width = 1, height = 1) {
		return !(x < 0 || x >= this.width ||
			y < 0 || y > this.height ||
			width <= 0 || x + width > this.width ||
			height <= 0 || y + height > this.height
		);
	}
}

import { Tile } from "./tile";
import { TileArea } from "./tilearea";

/**
 * A tile layer object containing tiles and other properties.
 */
export class TileLayer extends TileArea {
	/**
	 * TileLayer constructor.
	 *
	 * @param {TileMap} parentMap - The parent map for this layer.
	 * @param {object} options - Options for the layer. Can also be layer data you loaded from JSON or similar.
	 * @param {string} [options.name="Tilelayer"] - {@link TileLayer#name}
	 * @param {Tile[]} options.tiles=[] - {@link TileLayer#tiles}
	 * @param {Map<string, *>} options.properties=Map - {@link TileLayer#properties}
	 */
	constructor(parentMap, options) {
		super(parentMap.width, parentMap.height);

		/** The name of the tile layer.
		 * @type {string} */
		this.name = options.name || "Tilelayer";

		/** The tiles of the layer.
		 * @type {Tile[]} */
		this.tiles = [];
		for (let y = 0; y < parentMap.height; y++) {
			for (let x = 0; x < parentMap.width; x++) {
				const tile = new Tile(
					options.tiles
					? options.tiles[x % parentMap.width + y * parentMap.width]
					: { tileId: -1, tilesetId: 0 }
				);
				this.tiles.push(tile);
			}
		}

		/** Custom properties of the layer.
		 * @type {Map<string, *>} */
		this.properties = options.properties || new Map();
	}
}

import { TileLayer } from "./tilelayer";
import { TileSet } from "./tileset";

/**
 * A tilemap object with name, sizes and layers.
 */
export class TileMap {
	/**
     * TileMap constructor.
     *
     * @param {object} options - Options for the tilemap. Can also be a tilemap you loaded from JSON or similar.
	 * @param {string} [options.name="Tilemap"] - {@link TileMap#name}
     * @param {number} options.width - {@link TileMap#width}
     * @param {number} options.height - {@link TileMap#height}
     * @param {number} options.tileWidth - {@link TileMap#tileWidth}
     * @param {number} options.tileHeight - {@link TileMap#tileHeight}
     * @param {layerOption[]} [options.layers=[]] - Same structure as options in {@link TileLayer#constructor}
     * @param {tilesetOption[]} [options.tilesets=[]] - Same structure as options in {@link TileSet#constructor}
	 * @param {Map<string, *>} [options.properties=Map] - {@link TileMap#properties}
     */
	constructor(options) {
		/** The name of the tilemap.
		 * @type {string} */
		this.name = options.name || "Tilemap";

		/** The width of the tilemap.
		 * @type {number} */
		this.width = options.width;
		/** The height of the tilemap.
		 * @type {number} */
		this.height = options.height;

		/** The width of a tile for the tilemap.
		 * @type {number} */
		this.tileWidth = options.tileWidth;
		/** The height of a tile for the tilemap.
		 * @type {number} */
		this.tileHeight = options.tileHeight;

		/** All layers of the tilemap
		 *  @type {TileLayer[]} */
		this.layers = [];
		options.layers = options.layers || [];
		for (let layerData of options.layers) {
			this.createLayer(layerData);
		}

		/** The tilesets the tilemap consists of.
		 * @type {TileSet[]} */
		this.tilesets = [];
		options.tilesets = options.tilesets || [];
		for (let tileset of options.tilesets) {
			this.addTileset(new TileSet(tileset));
		}

		/** Custom properties of the tilemap.
		 * @type {Map<string, *>} */
		this.properties = options.properties || new Map();
	}

	/**
	 * Creates a new layer and adds it to the tilemap.
	 *
	 * @param {object} options - Options for the layer (see {@link TileLayer}).
	 *
	 * @returns {TileLayer}
	 */
	createLayer(options) {
		const layer = new TileLayer(this, options);
		this.layers.push(layer);
		return layer;
	}

	/**
	 * Adds a tileset to the tilemap.
	 *
	 * @param {TileSet} tileset - the tileset to add to the map.
	 *
	 * @returns {TileSet}
	 */
	addTileset(tileset) {
		this.tilesets.push(tileset);
		return tileset;
	}

	/**
	 * Converts this tilemap to a JSON string.
	 *
	 * @returns {string} - The JSON serialization of this tilemap.
	 */
	getJSON() {
		return JSON.stringify(this, (key, value) => {
			if (key.indexOf("_") === 0) {
				return undefined;
			}
			return value;
		});
	}
}

/**
 * A tileset object for storing arbitrary information about tilesets.
 */
export class TileSet {
	/**
	 * TileSet constructor.
	 *
	 * @param {object} options - The options for the tileset.
	 * @param {string} [options.name="Tileset"] - {@link TileSet#name}
	 * @param {string} [options.type="test"] - {@link TileSet#type}
	 * @param {string} [options.path] - {@link TileSet#path}
	 * @param {Map<string, *>} [options.properties] - {@link TileSet@properties}
	 */
	constructor({ name = "Tileset", type = "test", path, properties }) {
		/** The name of the tileset.
		 * @type {string} */
		this.name = name;
		/** The type of the tileset.
		 * Possible values are: "test", "image"
		 * @type {string} */
		this.type = type;
		/** The path of the tileset.
		 * This should only be used if tileset type is set to "image".
		 * @type {string} */
		this.path = path;

		/** Custom properties of the tilemap.
		 * @type {Map<string, *>} */
		this.properties = properties || new Map();
	}
}

import { should } from "chai";

import { Tile } from "../src";

should();

/** @test {Tile} */
describe("TileMap", () => {
	/** @test {Tile#constructor} */
	describe("#create", () => {
		it("creates from objects", () => {
			let tile = new Tile({
				tileId: 56,
				tilesetId: 1
			});
			tile.tileId.should.equal(56);
			tile.tilesetId.should.equal(1);
		});
		it("creates from strings", () => {
			let tile = new Tile("224:5");
			tile.tileId.should.equal(224);
			tile.tilesetId.should.equal(5);
		});
	});

	/** @test {Tile#setTile} */
	describe("#setTile", () => {
		let tile;
		beforeEach(() => {
			tile = new Tile("10:8");
		});

		it("can replace only tile ID", () => {
			tile.setData(15);
			tile.tileId.should.equal(15);
			tile.tilesetId.should.equal(8);
		});
		it("can replace both tile ID and tileset ID", () => {
			tile.setData(84, 2);
			tile.tileId.should.equal(84);
			tile.tilesetId.should.equal(2);
		});
		it("doesn't replace if ID is -1", () => {
			tile.setData(-1, -1);
			tile.tileId.should.equal(10);
			tile.tilesetId.should.equal(8);
		});
		it("does replace empty if told so", () => {
			tile.setData(-1, -1, true);
			tile.tileId.should.equal(-1);
			tile.tilesetId.should.equal(-1);
		});
	});

	/** @test {Tile#toJSON} */
	describe("#toJSON", () => {
		it("stringifies with properties", () => {
			let tile = new Tile({
				tileId: 14,
				tilesetId: 3,
				properties: {
					"property": "is a string"
				}
			});
			JSON.stringify(tile).should.contain("\"property\":\"is a string\"");
		});
		it("stringifies without properties", () => {
			let tile = new Tile("456:3");
			JSON.stringify(tile).should.equal("\"456:3\"");
		});
	});

	/** @test {Tile#clone} */
	describe("#clone", () => {
		let tile;
		beforeEach(() => {
			tile = new Tile({
				tileId: 14,
				tilesetId: 3,
				properties: {
					"property": "is a string"
				}
			});
		});

		it("clones all the properties of a tile", () => {
			tile.clone().should.deep.equal(tile);
		});
		it("doesn't become the same tile", () => {
			tile.clone().should.not.equal(tile);
		});
	});
});

import { should } from "chai";

import { TileArea } from "../src";

should();

/** @test {TileArea} */
describe("TileArea", () => {
	const areaWidth = 100, areaHeight = 100;
	let tileArea, tiles;

	beforeEach(() => {
		tiles = [];
		for (let i = 0; i < areaWidth * areaHeight; i++) {
			tiles.push({
				tileId: parseInt(Math.random() * 100)
			});
		}

		tileArea = new TileArea(areaWidth, areaHeight, tiles);
	});

	/** @test {TileArea#getTileIndex} */
	describe("#getTileIndex", () => {
		it("returns the right index", () => {
			tileArea.getTileIndex(16, 56).should.equal(5616);
			tileArea.getTileIndex(76, 32).should.equal(3276);
		});
		it("crashes if out of bounds", () => {
			(() => {
				tileArea.getTileIndex(areaWidth, 0);
			}).should.throw(RangeError);
			(() => {
				tileArea.getTileIndex(-1, 0);
			}).should.throw(RangeError);
		});
	});

	/** @test {TileArea#getTileArea} */
	describe("#getTileArea", () => {
		it("returns the correct tiles", () => {
			const tileAreaSplice = tileArea.getTileArea(0, 0, 2, 2);
			for (let x = 0; x < tileAreaSplice.width; x++) {
				for (let y = 0; y < tileAreaSplice.width; y++) {
					tileAreaSplice.tiles[tileAreaSplice.getTileIndex(x, y)]
						.tileId.should.equal(tiles[x + y * areaWidth].tileId);
				}
			}
		});
		it("returns the correct dimmensions", () => {
			const tileAreaSplice = tileArea.getTileArea(0, 5, 3, 2);
			tileAreaSplice.width.should.equal(3);
			tileAreaSplice.height.should.equal(2);
		});
		it("crashes if out of bounds", () => {
			(() => {
				tileArea.getTileArea(areaWidth, 0, 50);
			}).should.throw(RangeError);
			(() => {
				tileArea.getTileArea(areaWidth - 2, 0, 5, 1);
			}).should.throw(RangeError);
		});
		it("crashes if rectangle is invalid", () => {
			(() => {
				tileArea.getTileArea(0, 0, -1, 1);
			}).should.throw(RangeError);
			(() => {
				tileArea.getTileArea(0, 0, 1, 0);
			}).should.throw(RangeError);
		});
	});

	/** @test {TileArea#insertTileArea} */
	describe("#insertTileArea", () => {
		const smallAreaWidth = 3, smallAreaHeight = 3;
		let smallTileArea;

		beforeEach(() => {
			const tiles = [];
			for (let i = 0; i < smallAreaWidth * smallAreaHeight; i++) {
				tiles.push({
					tileId: parseInt(Math.random() * 100)
				});
			}
			smallTileArea = new TileArea(smallAreaWidth, smallAreaHeight, tiles);
		});

		it("inserts a tile area correctly", () => {
			const x = parseInt((areaWidth - smallAreaWidth) / 2);
			const y = parseInt((areaHeight - smallAreaHeight) / 2);
			tileArea.insertTileArea(x, y, smallTileArea);

			tileArea.getTileArea(x, y, smallAreaWidth, smallAreaHeight)
				.should.deep.equal(smallTileArea);
		});
		it("can skip empty (-1) tiles", () => {
			tileArea.insertTileArea(0, 0, new TileArea(1, 1, [{ tileId: -1 }]), true);

			tileArea.getTileArea(0, 0, 1, 1).tiles[0].tileId.should.not.equal(-1);
		});
		it("doesn't crash when going out of bounds", () => {
			const x = -smallAreaWidth + 1;
			const y = -smallAreaHeight + 1;
			tileArea.insertTileArea(x, y, smallTileArea);

			tileArea.getTileArea(0, 0, 1, 1).tiles[0].tileId.should.equal(
				smallTileArea.tiles[smallTileArea.tiles.length - 1].tileId
			);
		});
	});
});

import { should as chaiShould } from "chai";

import { TileMap } from "../src";
import testmap from "./testmap";

const should = chaiShould();

/** @test {TileMap} */
describe("TileMap", () => {
	const mapName = "a map name",
		mapWidth = 10,
		mapHeight = 10,
		tileWidth = 16,
		tileHeight = 16;

	let tilemap;

	beforeEach(() => {
		tilemap = new TileMap({
			name: mapName,
			width: mapWidth,
			height: mapHeight,
			tileWidth,
			tileHeight,

			layers: [
				{}, {}, {}
			]
		});
	});

	/** @test {TileMap#constructor} */
	describe("#create", () => {
		it("has a name", () => {
			tilemap.name.should.equal(mapName);
		});
		it("has dimmensions", () => {
			tilemap.width.should.be.a("number");
			tilemap.height.should.be.a("number");

			tilemap.width.should.equal(mapWidth);
			tilemap.height.should.equal(mapHeight);
		});
		it("has tile size", () => {
			tilemap.tileWidth.should.be.a("number");
			tilemap.tileHeight.should.be.a("number");
		});
		it("has layers", () => {
			tilemap.layers.should.have.length(3);
		});
		it("has properties for layers", () => {
			tilemap.layers[0].name.should.be.a("string");
		});
		it("reads a valid tilemap JSON", () => {
			tilemap = new TileMap(testmap);
			tilemap.name.should.equal("test map");
			tilemap.layers[0].tiles[23].tileId.should.equal(1);
		});
		it("has correct default values", () => {
			const tilemap = new TileMap({});
			tilemap.name.should.be.a("string");
			tilemap.layers.should.be.an("array");
		});
	});

	/** @test {TileMap#getJSON} */
	describe("#getJSON", () => {
		it("returns a valid JSON string", () => {
			JSON.parse(tilemap.getJSON());
		});
		it("doesn't parse private properties", () => {
			tilemap._property = "foo";
			should.not.exist(JSON.parse(tilemap.getJSON())._property);
		});
	});
});

import { should as chaiShould } from "chai";

import { TileSet } from "../src";

const should = chaiShould();

/** @test {TileMap} */
describe("TileMap", () => {
	/** @test {TileMap#constructor} */
	describe("#create", () => {
		it("creates with default values", () => {
			const tileset = new TileSet({});
			tileset.name.should.be.a("string");
			tileset.type.should.be.a("string");
			should.not.exist(tileset.path);
		});
		it("creates with definded values", () => {
			const name = "hello",
				type = "image",
				path = "./some/path.png",
				tileset = new TileSet({ name, type, path });
			tileset.name.should.equal(name);
			tileset.type.should.equal(type);
			tileset.path.should.equal(path);
		});
	});
});
